import React from 'react'
import {Link} from "react-router-dom"
import {Formik, Form, Field, ErrorMessage} from 'formik';
import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from "axios";
import * as Yup from 'yup';

function GenerateMealPlan() {
    const email = sessionStorage.getItem("email");

    const [listOfRecipes, setlistOfRecipes] = useState([]);
    const [listOfConstraintedRecipes, setlistOfConstraintedRecipes] = useState([]);




    useEffect(() => {
        axios.get("http://localhost:3001/recipes").then((response) => {
          setlistOfRecipes(response.data);
        });
      }, []);

    const initialValues = {
        name: "",
        type: "",
    };



    const onSubmit = (data) => {

        let score;
        let constraints;

        axios.get(`http://localhost:3001/authentication/${email}`).then((response) => {
            const userId = response.data;
            axios.get(`http://localhost:3001/profiles/${userId}`).then((response) => {
                 score = response.data.score;
                 constraints = response.data.constraints;
                 //constraints = ["peanuts-free"];
                 console.log(score);
                 if (constraints) {
                    const constraintedRecipes =  listOfRecipes.filter((recipe) =>
                      constraints.includes(recipe.category)
                    );
                    setlistOfConstraintedRecipes(constraintedRecipes);
                  } else {
                    setlistOfConstraintedRecipes(listOfRecipes);
                  }
                  if (listOfConstraintedRecipes.length > 0) {
                    listOfConstraintedRecipes.forEach((recipe) => {
                      axios.get(`http://localhost:3001/recipes/portion/4`).then((response) => {
                        // Process the response for each recipe
                            const output = response.data;
                            console.log(response.data);
                        });
                    });
                  }

              });

        });


    };



    const validationSchema = Yup.object().shape({
        name: Yup.string().required(),
        type: Yup.string().required(),

    });


  return (
    <div className='createProfilePage'>
        <Link to="/">Back</Link>
        <div className='createProfilePageForm'>
            <Formik initialValues={initialValues} onSubmit={onSubmit} validationSchema={validationSchema}>
                <Form>
                    <label>Meal Plan Name: </label>
                    <ErrorMessage name="name" component="span"/>
                    <Field autoComplete="off" id="inputGenerateMealPlan" name="name" placeholder="Example: 19 June Monday " />

                    <label>Goal : </label>
                    <ErrorMessage name="type" component="span"/>
                    <Field as="select" id="inputGenerateMealPlan" name="type">
                        <option value="cut">Loose Weight</option>
                        <option value="mantain">Maintain</option>
                        <option value="bulk">Gain Weight</option>
                    </Field>
                    

                    <button type="submit">Generate MealPlan</button>
                </Form>
            </Formik>
        </div>
    </div>
  )
}

export default GenerateMealPlan
