import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import axios from 'axios';

function Product() {
    let {id} = useParams();
    const [productObject, setProductObject] = useState({});
    

    useEffect(() => {
        axios.get(`http://localhost:3001/products/byId/${id}`).then((response) => {
        setProductObject(response.data); 
      });
      }, []);

  return (
    <div className='productPage'>
    <div className='name'>{productObject.name}</div>
    <div className='body'>
      <div className='portion'>Unit of measurement: {productObject.portion}</div>
      <div className='caloriesPerPortion'> Calories: {productObject.caloriesPerPortion}</div>
    </div>
    <div className='category'>{productObject.category}</div>
  </div>
  )
}

export default Product
