import React from 'react';
import {Formik, Form, Field, ErrorMessage, FieldArray, FormikProps} from 'formik';
import * as Yup from 'yup';
import axios from "axios";
import { useEffect, useState } from 'react';



function AddProduct() {

    const [listOfProducts, setListOfProducts] = useState([]);


    const initialValues = {
        name: "",
        category: "",
        totalCalories: "",
        quantity: "",
        ingredients: []
    };

    
    const onSubmit = (data) => {
            axios.post("http://localhost:3001/products", data).then((response) => {
                setListOfProducts(response.data);
              console.log("test working");
            });
    };


    const validationSchema = Yup.object().shape({
        name: Yup.string().required(),
        category: Yup.string().required(),
        quantity: Yup.string().required(),
        totalCalories: Yup.number().required(),
        ingredients: Yup.array().of(
            Yup.object().shape({
                name: Yup.string().required(),
                quantity: Yup.string().required()
            })
        )
    });


  return ( 
    <div className="addProductPage">
      <Formik initialValues={initialValues} onSubmit={onSubmit} validationSchema={validationSchema}>
        <Form>
            <label>Product Name: </label>
            <ErrorMessage name="name" component="span"/>
            <Field autoComplete="off" id="inputAddProduct" name="name" placeholder="Example: Coca Cola" />
            <label>Category: </label>
            <ErrorMessage name="category" component="span"/>
            <Field autoComplete="off" id="inputAddProduct" name="category" placeholder="Example: sugars " />
            <label>Portion Type: </label>
            <ErrorMessage name="quantity" component="span"/>
            <Field autoComplete="off" id="inputAddProduct" name="quantity" placeholder="Example: 2.5" />

            {/* <FieldArray>
                {({ push, remove }) => (
                  <>
                    <label>Ingredients: </label>
                  </>
                )}
            </FieldArray> */}

            <label>Ingredients:</label>
            {FormikProps.values.ingredients.map((ingredient, index) => (
              <div key={index}>
                <Field name={`ingredients[${index}].name`} placeholder="Ingredient Name" />
                <ErrorMessage name={`ingredients[${index}].name`} component="span" />
                <Field name={`ingredients[${index}].quantity`} placeholder="Ingredient Quantity" />
                <ErrorMessage name={`ingredients[${index}].quantity`} component="span" />
              </div>
            ))}

            <button type="button" onClick={() => formikProps.values.ingredients.push({ name: '', quantity: '' })}>
              Add Ingredient
            </button>

            {/* <label>Calories no.: </label>
            <ErrorMessage name="totalCalories" component="span"/>
            <Field autoComplete="off" id="inputAddProduct" name="totalCalories" placeholder="Example: 86" /> */}
            <button type="submit">Add Product</button>
        </Form>
       </Formik>
    </div>
  );
}

export default AddProduct;
